'use strict';

const OperationBase = require('mongodb/lib/operations/operation').OperationBase;
const deleteCallback = require('mongodb/lib/operations/common_functions').deleteCallback;
const removeDocuments = require('mongodb/lib/operations/common_functions').removeDocuments;

class DeleteOneOperation extends OperationBase {
  constructor(collection, filter, options) {
    super(options);

    this.collection = collection;
    this.filter = filter;
  }

  execute(callback) {
    const coll = this.collection;
    const filter = this.filter;
    const options = this.options;

    options.single = true;
    removeDocuments(coll, filter, options, (err, r) => deleteCallback(err, r, callback));
  }
}

module.exports = DeleteOneOperation;
