'use strict';

const Aspect = require('mongodb/lib/operations/operation').Aspect;
const defineAspects = require('mongodb/lib/operations/operation').defineAspects;
const DropIndexOperation = require('mongodb/lib/operations/drop_index');
const handleCallback = require('mongodb/lib/utils').handleCallback;

class DropIndexesOperation extends DropIndexOperation {
  constructor(collection, options) {
    super(collection, '*', options);
  }

  execute(callback) {
    super.execute(err => {
      if (err) return handleCallback(callback, err, false);
      handleCallback(callback, null, true);
    });
  }
}

defineAspects(DropIndexesOperation, Aspect.WRITE_OPERATION);

module.exports = DropIndexesOperation;
