'use strict';

let collection;
let cursor;
let db;

function loadCollection() {
  if (!collection) {
    collection = require('mongodb/lib/collection');
  }
  return collection;
}

function loadCursor() {
  if (!cursor) {
    cursor = require('mongodb/lib/cursor');
  }
  return cursor;
}

function loadDb() {
  if (!db) {
    db = require('mongodb/lib/db');
  }
  return db;
}

module.exports = {
  loadCollection,
  loadCursor,
  loadDb
};
