'use strict';

const CommandOperation = require('mongodb/lib/operations/command');
const handleCallback = require('mongodb/lib/utils').handleCallback;

class ReIndexOperation extends CommandOperation {
  constructor(collection, options) {
    super(collection.s.db, options, collection);
  }

  _buildCommand() {
    const collection = this.collection;

    const cmd = { reIndex: collection.collectionName };

    return cmd;
  }

  execute(callback) {
    super.execute((err, result) => {
      if (callback == null) return;
      if (err) return handleCallback(callback, err, null);
      handleCallback(callback, null, result.ok ? true : false);
    });
  }
}

module.exports = ReIndexOperation;
